import './Panel.css';

import React from 'react';
import Button from 'react-bootstrap/Button';

function Video() {
  return <div className="project1-video">
    <iframe width="560" height="315" src="https://www.youtube.com/embed/MEW_JCmHTo0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe></div>;
}

function Image(image) {
  return <div className="project1-image"><img src={image}></img></div>;
}

function isImage(i, v) {
  if (i) {
    return <Image image={i} />;
  } else if (v)
  return <Video />
}

const Panel = ({ heading, video, image, description, link, btnTitle, skills }) => {
  return (
    <div className="projects">
      <div className="project1">
        <h3>{heading}</h3>
        <isImage i={image} v={video} />;
        <div className="project1-video">
          <iframe width="560" height="315" src="https://www.youtube.com/embed/MEW_JCmHTo0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
        </div>
        <div className="project1-info">
          <p>{description}</p>
          <Button href={link} variant="light" className="btn-light">
            {btnTitle}
          </Button>
          <p>{skills}</p>
        </div>
      </div>
    </div>
  );
}

export default Panel;
